function initPage(){document.querySelector(".menu-trigger").addEventListener("click",function(){document.querySelector(".menu-container").classList.toggle("closed")}),document.querySelectorAll(".bubble-link").forEach(function(e){var t=e;e.addEventListener("click",function(e){t.classList.remove("radiate"),t.classList.add("radiate"),setTimeout(function(){t.classList.remove("radiate")},300)})})}document.addEventListener("DOMContentLoaded",initPage);